INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-04-29 03:44:11', '2016-04-29 02:44:11', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2016-04-29 03:44:11', '2016-04-29 02:44:11', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2016-04-29 03:44:11', '2016-04-29 02:44:11', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://localhost/wordpress/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2016-04-29 03:44:11', '2016-04-29 02:44:11', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(4, 1, '2016-04-29 03:46:38', '2016-04-29 02:46:38', 'This summer is great with 40 degree Celsius, We are chilling and working well. Rivers are drying up and cattle peering for water. God should bestow his salvation on us.', 'This is our new post. We are working in this hot summer and chilling well.', '', 'publish', 'open', 'open', '', 'this-is-our-new-post-we-are-working-in-this-hot-summer-and-chilling-well', '', '', '2016-04-29 03:46:38', '2016-04-29 02:46:38', '', 0, 'http://localhost/wordpress/?p=4', 0, 'post', '', 0),
(5, 1, '2016-04-29 03:46:38', '2016-04-29 02:46:38', 'This summer is great with 40 degree Celsius, We are chilling and working well. Rivers are drying up and cattle peering for water. God should bestow his salvation on us.', 'This is our new post. We are working in this hot summer and chilling well.', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2016-04-29 03:46:38', '2016-04-29 02:46:38', '', 4, 'http://localhost/wordpress/index.php/2016/04/29/4-revision-v1/', 0, 'revision', '', 0),
(6, 1, '2016-04-29 03:48:18', '2016-04-29 02:48:18', 'So what to say ? They are not taking interest in meeting and facing each other. Its all mobile journey. Scolding on mobile, love on mobile everything is on mobile..', 'Our new article on people communicate', '', 'publish', 'open', 'open', '', 'our-new-article-on-people-communicate', '', '', '2016-04-29 03:48:18', '2016-04-29 02:48:18', '', 0, 'http://localhost/wordpress/?p=6', 0, 'post', '', 0),
(7, 1, '2016-04-29 03:48:18', '2016-04-29 02:48:18', 'So what to say ? They are not taking interest in meeting and facing each other. Its all mobile journey. Scolding on mobile, love on mobile everything is on mobile..', 'Our new article on people communicate', '', 'inherit', 'closed', 'closed', '', '6-revision-v1', '', '', '2016-04-29 03:48:18', '2016-04-29 02:48:18', '', 6, 'http://localhost/wordpress/index.php/2016/04/29/6-revision-v1/', 0, 'revision', '', 0),
(8, 1, '2016-04-29 04:46:33', '2016-04-29 03:46:33', 'So what to say ? They are not taking interest in meeting and facing each other. Its all mobile journey. Scolding on mobile, love on mobile everything is on mobile..  <a href="http://localhost/test/getmore">Get More</a>', 'Our new article on people communicate', '', 'inherit', 'closed', 'closed', '', '6-autosave-v1', '', '', '2016-04-29 04:46:33', '2016-04-29 03:46:33', '', 6, 'http://localhost/wordpress/index.php/2016/04/29/6-autosave-v1/', 0, 'revision', '', 0);
